import React, {useState, useEffect, useRef} from 'react'
import axios from "axios"
import {preventDefault} from "react"
import {Navigate} from "react-router-dom"
import { Link } from 'react-router-dom'

export default function Table({Url}) {

    const [isUrl,setisUrl] = useState((false))
    const clickedUrl=useRef;
    const [url]=clickedUrl.current.value;
   
    const shorturlHandler= async (e)=>{
        e.preventDefault();
        const url={
            short:clickedUrl.current.value,
            full:clickedUrl.current.value,
            clicks:clickedUrl.current.value,
        }
        
        try{
            
            console.log("clickedd")
            console.log(url)
            await axios.get("/shorturls/"+url.short)
                   
        }
        catch(err){
            console.log(err)
        }
    }
    console.log(isUrl)
  
        return (
            <table className='table table-striped table-responsive'>
                <thead>
                    <tr>
                        <th scope="col">Full URL</th>
                        <th scope="col">Short URL</th>
                        <th scope="col">Notes</th>
                        <th scope="col">Clicks</th>
                    </tr>
                </thead>
                <tbody>
                    
                       {Url.map((url)=>(
                            <tr scope="row"> 
                                <td >
                                <a href={url.full}>{url.full}</a>
                                </td>
                                <td ref={clickedUrl} onClick={shorturlHandler}><Link  >{url.short}</Link></td>
                                <td>{url.note}</td>
                                <td>{url.clicks}</td>
                            </tr>
                       ))} 
                   
                </tbody>
               

            </table>  
  )
}
