import React,{useState, useEffect, useContext} from 'react'
import "./home.css"
import axios from "axios"
import {AuthContext} from "../../context/AuthContext"
import Table from "../../components/Table/Table"

export default function Home() {
    const {user}=useContext(AuthContext)
    const [Url,setUrl] =useState([]);



    // const [user,setUser] =useState({});
    
    //to fetch the user
    // useEffect(()=>{
    //     const fetchUser= async () =>{
    //         const res= await axios.get("users/648a316b6099abdc78c36818")
    //         setUser(res.data);
    //     }
    //     fetchUser();
    // },[])
    console.log(user)

    useEffect(()=>{
        const fetchUrl= async () =>{
            try{
            console.log(user.username)
            const res= await axios.get("/shorturls/"+user.username)
            console.log(res.data)
            setUrl(res.data);
            }
            catch(err){
                console.log(err)
            }
        }
        fetchUrl();
    },[user._id, user.username])

    console.log(Url)
  return (
    <div>
        <h1>
            URL Shortner
        </h1>
        <div className="container">
            <form action="" className='my-4 form-inline'>
                <label htmlFor="" className='sr-only'>Full URL</label>
                <input placeholder='URL' type="url" className='form-control col mr-2' />
                <button className='btn btn-success'>Submit</button>
            </form>
            <table className='table table-striped table-responsive'>
                <thead>
                    <tr>
                        <th scope="col">Full URL</th>
                        <th scope="col">Short URL</th>
                        <th scope="col">Clicks</th>
                    </tr>
                    <tr> 
                        {Url.map(url=>(
                            <div>  
                                <td>
                                <a href={url.full}>{url.full}</a>
                                </td>
                                <td><a href={url.short}>{url.short}</a></td>
                                <td>{url.clicks}</td>
                            </div>
                        ))}
                    </tr>
                </thead>
            </table>

            
      ))}
            
        </div>
    </div>
  )
}
