import React,{useState, useEffect, useContext} from 'react'
import "./home.css"
import axios from "axios"
import {AuthContext} from "../../context/AuthContext"
import Table from "../../components/Table/Table"
import {Navigate} from "react-router-dom"
import Search from '../../components/Search/Search'


export default function Home() {
    const {user}=useContext(AuthContext)
    const [Url,setUrl] =useState([]);
    const [query,setQuery]= useState("");
    // const [items,setItems] = useState([[Url]])
    // const [filteredItems,setFilteredItems] = useState([Url])
    // const [clicks,setClicks]=useState([]);
    // const [user,setUser] =useState({});
    //to fetch the user
    // useEffect(()=>{
    //     const fetchUser= async () =>{
    //         const res= await axios.get("users/648a316b6099abdc78c36818")
    //         setUser(res.data);
    //     }
    //     fetchUser();
    // },[])

    console.log(user)

    useEffect(()=>{
        const fetchUrl= async () =>{
            try{
            console.log(user.username)
            const res= await axios.get("/shorturls/get/"+user.username)
            console.log(res.data)
            setUrl(res.data);
            }
            catch(err){
                console.log(err)
            }
        }
        fetchUrl();
    },[user._id, user.username])

    // function onChange(e){

    //     const value=e.target.value;
    //     setFilteredItems(Url.filter(item=>
    //         item.toLowerCase().includes(value.toLowerCase())))
    // }

  console.log(Url)
  return (
    <div>
        <h1>
            URL Shortner
        </h1>
        <div className="container">
            <form action="" className='my-4 form-inline'>
                <label htmlFor="" className='visually-hidden'>Full URL</label>
                <input placeholder='URL' type="url" className='form-control col mr-2' />
                <button className='btn btn-success'>Submit</button>
            </form>
                Search:
            <input onChange={(e)=>setQuery(e.target.value)} type="text" />
            <br />
            <br />

            {/* <form onSubmit={onSubmit} action="">
                New Item : <input ref={inputRef} type="text" />
                <button type='submit'>ADD</button>
            </form> */}
            {/* {console.log(items)} */}
            <h3>ITEMS:</h3>
            {/* {filteredItems.map(item=>(
                <div>{item}</div>
            ))} */}
            <table className='table table-striped table-responsive'>
                <thead>
                    <tr>
                        <th scope="col">Full URL</th>
                        <th scope="col">Short URL</th>
                        <th scope="col">Clicks</th>
                    </tr>
                </thead>
                <tbody>
                        
                        {Url.map(p=>(
                         <Table key={p._id} url={p}/> ))}
                </tbody>
               

            </table>          
        </div>
    </div>
  )
}
