import React,{useState, useEffect} from 'react'
import "./home.css"
import axios from "axios"

export default function 
() {

    const [user,setUser] =useState({});
    const [Url,setUrl] =useState({});
    //to fetch the user
    useEffect(()=>{
        const fetchUser= async () =>{
            const res= await axios.get("users/648a316b6099abdc78c36818")
            setUser(res.data);
        }
        fetchUser();
    },[])
    console.log(user)

    useEffect(()=>{
        const fetchUrl= async () =>{
            try{
                const res= await axios.get("shortURLs/"+user.username)
            console.log(res)
            setUrl(res);
            }
            catch(err){
                console.log(err)
            }
        }
        fetchUrl();
    },[user._id])
  return (
    <div>
        <h1>
            URL Shortner
        </h1>
        <div className="container">
            <form action="" className='my-4 form-inline'>
                <label htmlFor="" className='sr-only'>Full URL</label>
                <input placeholder='URL' type="url" className='form-control col mr-2' />
                <button className='btn btn-success'>Submit</button>
            </form>
            <table className='table table-striped table-responsive'>
                <thead>
                    <tr>
                        <th>Full URL</th>
                        <th>Short URL</th>
                        <th>Clicks</th>
                    </tr>
                    <tr>
                        <td>
                            <a href="https://www.youtube.com/watch?v=zM93yZ_8SvE&t=6361s">https://www.youtube.com/watch?v=zM93yZ_8SvE&t=6361s</a>
                        </td>
                        <td><a href="/123345">/123456</a></td>
                        <td>15</td>
                    </tr>
                </thead>
            </table>
        </div>
    </div>
  )
}
